{"title":"False Positive Distribution","type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "title": {
    "text": "False Positive Likelihood Distribution",
    "fontSize": 18,
    "anchor": "middle",
    "font": "Segoe UI",
    "color": "#222222"
  },
  "data": {
    "url": {
      "index": "wazuh-enriched-alerts",
      "body": {
        "size": 1000,
        "_source": ["llm_enrichment.false_positive_likelihood"],
        "query": { "exists": { "field": "llm_enrichment.false_positive_likelihood" } }
      }
    },
    "format": { "property": "hits.hits" }
  },
  "transform": [
    { "filter": "isValid(datum._source.llm_enrichment.false_positive_likelihood)" },
