{"title":"Enrichment Time by Category","type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "title": {
    "text": "Enrichment Time by Category",
    "fontSize": 18,
    "anchor": "start",
    "font": "Segoe UI",
    "color": "#222222"
  },
  "data": {
    "url": {
      "index": "wazuh-enriched-alerts",
      "body": {
        "size": 0,
        "aggs": {
          "categories": {
            "terms": {
              "field": "llm_enrichment.alert_category.keyword",
              "size": 10,
              "order": { "avg_time": "desc" }
            },
            "aggs": {
              "avg_time": {
                "avg": {
                  "field": "llm_enrichment.enrichment_duration_ms"
                }
              }
            }
          }
        },
        "query": {
          "bool": {
            "must_not": [
              { "term": { "llm_enrichment.alert_category.keyword": "error" } }
            ]
          }
        }
      }
    },
    "format": {
      "property": "aggregations.categories.buckets"
    }
  },
  "mark": {
    "type": "bar",
    "tooltip": true,
    "cornerRadius": 1000,
    "cornerRadiusEnd": 1000,
    "fill": {
      "x1": 0,
      "y1": 0,
      "x2": 1,
      "y2": 0,
      "gradient": "linear",
      "stops": [
        { "offset": 0, "color": "#87cefa" },
        { "offset": 1, "color": "#1e90ff" }
      ]
    }
  },
  "encoding": {
    "y": {
      "field": "key",
      "type": "ordinal",
      "axis": {
        "title": null,
        "labelFontSize": 14,
        "labelFont": "Segoe UI",
        "labelColor": "#222222",
        "labelAlign": "center",
        "labelPadding": 70,
        "grid": false,
        "domain": false,
        "ticks": false
      }
    },
    "x": {
      "field": "avg_time.value",
      "type": "quantitative",
      "axis": {
        "title": null,
        "labels": false,
        "ticks": false,
        "grid": false,
        "domain": false
      }
    },
    "tooltip": [
      {
        "field": "key",
        "type": "nominal",
        "title": "Category"
      },
      {
        "field": "avg_time.value",
        "type": "quantitative",
        "title": "Avg Enrichment Time (ms)",
        "format": ".1f"
      }
    ]
  },
  "config": {
    "background": "#ffffff",
    "axis": {
      "domainColor": "#cccccc",
      "tickColor": "#cccccc",
      "labelColor": "#222222",
      "titleColor": "#222222"
    },
    "title": {
      "color": "#222222"
    },
    "style": {
      "cell": {
        "stroke": "transparent"
      }
    }
  },
  "usermeta": {
    "vegaDarkMode": {
      "background": "#1e1e1e",
      "axis": {
        "domainColor": "#444444",
        "tickColor": "#444444",
        "labelColor": "#eeeeee",
        "titleColor": "#eeeeee"
      },
      "title": {
        "color": "#ffffff"
      },
      "style": {
        "cell": {
          "stroke": "transparent"
        }
      }
    }
  }
}
"}}

{"title":"Tags Cloud","type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  // Title removed
  "data": {
    "url": {
      "index": "wazuh-enriched-alerts",
      "body": {
        "size": 1000,
        "_source": ["llm_enrichment.tags"],
        "query": { "exists": { "field": "llm_enrichment.tags" } }
      }
    },
    "format": { "property": "hits.hits" }
  },
  "transform": [
    { "flatten": ["_source.llm_enrichment.tags"] },
    { "aggregate": [{ "op": "count", "as": "tag_count" }], "groupby": ["_source.llm_enrichment.tags"] },
    { "calculate": "random() * (400 - 64)", "as": "random_x" },
    { "calculate": "random() * (300 - 64)", "as": "random_y" }
  ],
  "mark": {
    "type": "text",
    "font": "Segoe UI",
    "fontWeight": "bold"
  },
  "encoding": {
    "text": { "field": "_source.llm_enrichment.tags", "type": "nominal" },
    "size": { "field": "tag_count", "type": "quantitative", "scale": { "range": [16, 64] } },
    "color": {
      "field": "tag_count",
      "type": "quantitative",
      "scale": { "scheme": "blues" },
      "legend": null
    },
    "x": { "field": "random_x", "type": "quantitative", "axis": null },
    "y": { "field": "random_y", "type": "quantitative", "axis": null }
  },
  "config": {
    "background": "#ffffff",
    "view": { "stroke": null }
  },
  "usermeta": {
    "vegaDarkMode": {
      "background": "#1e1e1e",
      "title": { "color": "#ffffff" }
    }
  }
}

"}}

{"title":"Alerts Distribution","type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "title": {
    "text": "Alerts Over Time",
    "fontSize": 18,
    "anchor": "middle",
    "font": "Segoe UI",
    "color": "#222222"
  },
  "data": {
    "url": {
      "%context%": true,
      "%timefield%": "@timestamp",
      "index": "wazuh-enriched-alerts",
      "body": {
        "size": 0,
        "aggs": {
          "alerts_over_time": {
            "date_histogram": {
              "field": "@timestamp",
              "calendar_interval": "minute"
            }
          }
        }
      }
    },
    "format": {
      "property": "aggregations.alerts_over_time.buckets"
    }
  },
  "mark": {
    "type": "bar",
    "cornerRadiusTopLeft": 8,
    "cornerRadiusTopRight": 8,
    "tooltip": true,
    "color": {
      "x1": 0,
      "y1": 0,
      "x2": 0,
      "y2": 1,
      "gradient": "linear",
      "stops": [
        { "offset": 0, "color": "#1e90ff" },
        { "offset": 1, "color": "#b3dafe" }
      ]
    },
    "bar": { "width": 20 }
  },
  "encoding": {
    "x": {
      "field": "key_as_string",
      "type": "temporal",
      "axis": {
        "title": null,
        "labelFontSize": 12,
        "labelFont": "Segoe UI",
        "labelColor": "#222222",
        "grid": false,
        "format": "%b %d %H:%M"
      }
    },
    "y": {
      "field": "doc_count",
      "type": "quantitative",
      "axis": null
    },
    "tooltip": [
      {
        "field": "key_as_string",
        "type": "temporal",
        "title": "Time"
      },
      {
        "field": "doc_count",
        "type": "quantitative",
        "title": "Alert Count"
      }
    ]
  },
  "config": {
    "background": "#ffffff",
    "axis": {
      "domainColor": "#cccccc",
      "tickColor": "#cccccc",
      "labelColor": "#222222",
      "titleColor": "#222222"
    },
    "title": {
      "color": "#222222"
    },
    "style": {
      "cell": {
        "stroke": "transparent"
      }
    }
  },
  "usermeta": {
    "vegaDarkMode": {
      "background": "#1e1e1e",
      "axis": {
        "domainColor": "#444444",
        "tickColor": "#444444",
        "labelColor": "#eeeeee",
        "titleColor": "#eeeeee"
      },
      "title": {
        "color": "#ffffff"
      },
      "style": {
        "cell": {
          "stroke": "transparent"
        }
      }
    }
  }
}"}}
